import React, { useEffect, useState } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';

const AdminLayout = () => {
  const navigate = useNavigate();
  const [item, setItem] = useState({});
  const [photo, setPhoto] = useState("");
  const defaultPhoto = "https://cdn.pixabay.com/photo/2020/07/01/12/58/icon-5359553_640.png";

  useEffect(() => {
    const storedEmail = localStorage.getItem("Email");
    const storedPhoto = localStorage.getItem("Photo");

    if (!storedEmail) {
      navigate("/login");
    } else {
      setItem({ Email: storedEmail });
      fetchUserPhoto(storedEmail);
    }
  }, []);

  const fetchUserPhoto = async (email) => {
    try {
      const response = await fetch("http://localhost:5000/api/getUser", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ Email: email }),
      });

      const data = await response.json();
      if (response.ok) {
        setPhoto(data.Photo || defaultPhoto);
        localStorage.setItem("Photo", data.Photo || defaultPhoto);
      } else {
        console.error("User not found");
      }
    } catch (error) {
      console.error("Error fetching user photo:", error);
    }
  };

  const handleSignOut = () => {
    localStorage.clear();
    navigate("/blog");
  };

  return (
    <div style={{ fontFamily: 'Segoe UI, sans-serif' }}>
      <div className="container-fluid">
        <div className="row flex-nowrap">
          <div className="col-auto col-md-3 col-xl-2 px-sm-2 px-0 bg-gradient sidebar-custom">
            <div className="d-flex flex-column align-items-center align-items-sm-start px-3 pt-3 text-white min-vh-100">
              <a href="/" className="d-flex align-items-center pb-3 mb-md-0 me-md-auto text-white text-decoration-none">
                <span className="fs-4 d-none d-sm-inline fw-bold">Admin Menu</span>
              </a>

              <ul className="nav nav-pills flex-column mb-sm-auto mb-0 align-items-center align-items-sm-start w-100" id="menu">
                <li className="nav-item w-100">
                  <Link to="/admin/Enquiry" className="nav-link text-white px-2 hover-highlight">
                    <i className="fs-5 bi-house me-2"></i> <span className="d-none d-sm-inline">Home</span>
                  </Link>
                </li>

                {/* Dashboard Dropdown */}
                <li className="nav-item w-100">
                  <div className="dropdown w-100">
                    <a href="#" className="nav-link px-2 text-white dropdown-toggle hover-highlight" id="dropdownDashboard" data-bs-toggle="dropdown">
                      <i className="fs-5 bi-speedometer2 me-2"></i> <span className="d-none d-sm-inline">Dashboard</span>
                    </a>
                    <ul className="dropdown-menu dropdown-menu-dark shadow-sm">
                      <li><Link className="dropdown-item" to="/admin/viewcategory">Categories Dashboard</Link></li>
                      <li><Link className="dropdown-item" to="/admin/viewsubcategory">Sub-Categories Dashboard</Link></li>
                      <li><Link className="dropdown-item" to="/admin/viewsmallsubcategory">Small Sub-Categories Dashboard</Link></li>
                      <li><Link className="dropdown-item" to="/admin/vieworders">Order Dashboard</Link></li>
                    </ul>
                  </div>
                </li>

                {/* Blogs Dropdown */}
                <li className="nav-item w-100">
                  <div className="dropdown w-100">
                    <a href="#" className="nav-link px-2 text-white dropdown-toggle hover-highlight" id="dropdownBlogs" data-bs-toggle="dropdown">
                      <i className="fs-5 bi-journal-text me-2"></i> <span className="d-none d-sm-inline">Blogs</span>
                    </a>
                    <ul className="dropdown-menu dropdown-menu-dark shadow-sm">
                      <li><Link className="dropdown-item" to="/admindashboard/addBlogs">Add Blogs</Link></li>
                      <li><Link className="dropdown-item" to="/admin/viewBlogs">View Blogs</Link></li>
                    </ul>
                  </div>
                </li>

                {/* Products Dropdown */}
                <li className="nav-item w-100">
                  <a href="#submenu3" data-bs-toggle="collapse" className="nav-link text-white px-2 hover-highlight">
                    <i className="fs-5 bi-box me-2"></i> <span className="d-none d-sm-inline">Products</span>
                  </a>
                  <ul className="collapse nav flex-column ms-2" id="submenu3" data-bs-parent="#menu">
                    <li><a href="#" className="nav-link text-white px-2">Product 1</a></li>
                    <li><a href="#" className="nav-link text-white px-2">Product 2</a></li>
                    <li><a href="#" className="nav-link text-white px-2">Product 3</a></li>
                    <li><a href="#" className="nav-link text-white px-2">Product 4</a></li>
                  </ul>
                </li>

                <li className="nav-item w-100">
                  <Link to="/admin/getordersinadmin" className="nav-link text-white px-2 hover-highlight">
                    <i className="fs-5 bi-cart-check me-2"></i> <span className="d-none d-sm-inline">Customer Orders</span>
                  </Link>
                </li>

                {/* Categories Dropdown */}
                <li className="nav-item w-100">
                  <div className="dropdown w-100">
                    <a href="#" className="nav-link px-2 text-white dropdown-toggle hover-highlight" id="dropdownCategories" data-bs-toggle="dropdown">
                      <i className="fs-5 bi-tags me-2"></i> <span className="d-none d-sm-inline">Categories</span>
                    </a>
                    <ul className="dropdown-menu dropdown-menu-dark shadow-sm">
                      <li><Link className="dropdown-item" to="/admin/addcategories">Categories</Link></li>
                      <li><Link className="dropdown-item" to="/admin/addsubcategories">Sub-Categories</Link></li>
                      <li><Link className="dropdown-item" to="/admin/addsmallsubcategories">Small-Sub-Categories</Link></li>
                    </ul>
                  </div>
                </li>
              </ul>

              <hr className="text-white w-100" />

              {/* Profile Section */}
              <div className="dropdown pb-4">
                <a href="#" className="d-flex align-items-center text-white text-decoration-none dropdown-toggle" id="dropdownUser1" data-bs-toggle="dropdown">
                  <img src={photo || defaultPhoto} alt="profile" width="30" height="30" className="rounded-circle me-2" />
                  <span className="d-none d-sm-inline">{item.Email}</span>
                </a>
                <ul className="dropdown-menu dropdown-menu-dark shadow-sm">
                  <li><Link className="dropdown-item" to="#">New project...</Link></li>
                  <li><Link className="dropdown-item" to="#">Settings</Link></li>
                  <li><Link className="dropdown-item" to="/admin/profile">Profile</Link></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><Link className="dropdown-item" to="#" onClick={handleSignOut}>Sign out</Link></li>
                </ul>
              </div>
            </div>
          </div>

          <div className="col py-3 bg-light" style={{ minHeight: "100vh" }}>
            <Outlet />
          </div>
        </div>
      </div>

      {/* Custom CSS Inline or move this to .css file */}
      <style>{`
        .sidebar-custom {
          background: linear-gradient(180deg, #212529, #343a40);
          box-shadow: 2px 0 10px rgba(0,0,0,0.2);
        }
        .hover-highlight:hover {
          background-color: rgba(255,255,255,0.1);
          border-radius: 5px;
        }
        .nav-link {
          transition: background 0.2s ease;
        }
        .dropdown-menu-dark .dropdown-item:hover {
          background-color: #495057;
        }
      `}</style>
    </div>
  );
};

export default AdminLayout;
