// export default AdminLayout

import React, { useEffect, useState } from 'react'
import { Outlet, Link, useNavigate } from 'react-router-dom'

const AdminLayout = () => {
  const navigate = useNavigate();
  const [item, setItem] = useState({});
  const [photo, setPhoto] = useState("");
  const defaultPhoto = "https://cdn.pixabay.com/photo/2020/07/01/12/58/icon-5359553_640.png";

  useEffect(() => {
    const storedEmail = localStorage.getItem("Email");
    const storedPhoto = localStorage.getItem("Photo");

    if (!storedEmail) {
      navigate("/login");
    } else {
      setItem({ Email: storedEmail });
      fetchUserPhoto(storedEmail);
    }
  }, []);

  const fetchUserPhoto = async (email) => {
    try {
      const response = await fetch("http://localhost:5000/api/getUser", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ Email: email }),
      });

      const data = await response.json();
      if (response.ok) {
        setPhoto(data.Photo || defaultPhoto);
        localStorage.setItem("Photo", data.Photo || defaultPhoto);
      } else {
        console.error("User not found");
      }
    } catch (error) {
      console.error("Error fetching user photo:", error);
    }
  };

  const handleSignOut = () => {
    localStorage.clear();
    navigate("/blog");
  }

  return (
    <div style={{ overflow: "hidden" }}>
      <div className="container-fluid" style={{ padding: 0, margin: 0 }}>
        <div className="row" style={{ height: "100vh", overflow: "hidden" }}>
          {/* Sidebar */}
          <div
            className="col-12 col-md-3 col-xl-2 bg-dark text-white"
            style={{
              padding: "0 1rem",
              height: "100vh",
              overflowY: "auto",
              position: "fixed"
            }}
          >
            <div className="d-flex flex-column pt-3" style={{ height: "100%" }}>
              <h5 className="text-white mb-4">Menu</h5>
              <ul className="nav nav-pills flex-column mb-auto">
                <li className="nav-item">
                  <Link to="/admin/Enquiry" className="nav-link text-white">Home</Link>
                </li>
                <li className="nav-item dropdown">
                  <a className="nav-link dropdown-toggle text-white" data-bs-toggle="dropdown">Dashboard</a>
                  <ul className="dropdown-menu dropdown-menu-dark">
                    <li><Link className="dropdown-item" to="/admin/viewcategory">Categories Dashboard</Link></li>
                    <li><Link className="dropdown-item" to="/admin/viewsubcategory">Sub-Categories Dashboard</Link></li>
                    <li><Link className="dropdown-item" to="/admin/viewsmallsubcategory">Small-Sub-Categories Dashboard</Link></li>
                    <li><Link className="dropdown-item" to="/admin/vieworders">Order Dashboard</Link></li>
                  </ul>
                </li>
                <li className="nav-item dropdown">
                  <a className="nav-link dropdown-toggle text-white" data-bs-toggle="dropdown">Blogs</a>
                  <ul className="dropdown-menu dropdown-menu-dark">
                    <li><Link className="dropdown-item" to="/admindashboard/addBlogs">Add Blogs</Link></li>
                    <li><Link className="dropdown-item" to="/admin/viewBlogs">View Blogs</Link></li>
                  </ul>
                </li>
                <li>
                  <Link to="/admin/getordersinadmin" className="nav-link text-white">Customer Orders</Link>
                </li>
                <li className="nav-item dropdown">
                  <a className="nav-link dropdown-toggle text-white" data-bs-toggle="dropdown">Categories</a>
                  <ul className="dropdown-menu dropdown-menu-dark">
                    <li><Link className="dropdown-item" to="/admin/addcategories">Categories</Link></li>
                    <li><Link className="dropdown-item" to="/admin/addsubcategories">Sub-Categories</Link></li>
                    <li><Link className="dropdown-item" to="/admin/addsmallsubcategories">Small-Sub-Categories</Link></li>
                  </ul>
                </li>
              </ul>
              <hr />
              <div className="dropdown mt-auto mb-3">
                <a className="d-flex align-items-center text-white text-decoration-none dropdown-toggle" data-bs-toggle="dropdown">
                  <img src={photo || defaultPhoto} alt="admin" width="30" height="30" className="rounded-circle me-2" />
                  <strong>{item.Email}</strong>
                </a>
                <ul className="dropdown-menu dropdown-menu-dark">
                  <li><Link className="dropdown-item" to="/admin/profile">Profile</Link></li>
                  <li><hr className="dropdown-divider" /></li>
                  <li><button className="dropdown-item" onClick={handleSignOut}>Sign out</button></li>
                </ul>
              </div>
            </div>
          </div>

          {/* Main Content Area */}
          <div
            className="col offset-md-3 offset-xl-2"
            style={{
              padding: '1rem',
              height: "100vh",
              overflowY: "auto"
            }}
          >
            <Outlet />
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminLayout;
