import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';

const ViewBlogs = () => {
  
    const [items,setitems]=useState([]);
    useEffect(()=>{
        fetch("http://localhost:5000/api/viewblogs")
        .then(response=>response.json())
        // .then(data=>console.log(data))
        .then(data=>setitems(data))
        .catch(err=>console.log(err))
    },[]);

    
    const deleteblog=async(blogid)=>{
      const formdata=JSON.stringify({
        blogid:blogid,
      })
      try{
        const response=await fetch("http://localhost:5000/api/deleteblog",{
          headers:{"Content-type":"application/json"},
          method:"Post",
          body:formdata
        })

        if(response.ok){
          alert("blog deleted");
          // refresh krege taki blog delete ho jaye
          setTimeout(()=>{
            window.location.reload();
          },1000);
        }
        else{
          alert("error");
        }

      }
      catch(err){
       console.log(err);
      }
    }
    
  return (
    <div>
        <h1>View Blogs</h1>
      <table class="table  table-bordered p-5">
        <thead>
  
  
    <tr >
    <th>Title</th>
    <th>Photo</th>
    <th>Content</th>
    <th>createdAt</th>
    <th>Delete</th>
      
    </tr>
    </thead>
    <tbody>
    
    {
        items.map(item=>(
            <tr class="table-active" key={item._id}>
              
                <td>{item.title}</td>
                <td><img style={{backgroundSize:"cover",height:"50px"}}src={item.photo}></img></td>
                <td>{item.content}</td>
                
                <td>{item.createdAt}</td>
                <td>
                  <button className='btn btn-danger ' onClick={()=>deleteblog(item._id)}><i class="fa-solid fa-trash"></i></button>
                </td>
                </tr>
        )
    )
    }

    
  </tbody>
</table>
<button className='' ><Link to="/addBlogs">Add More</Link></button>
    </div>
  )
}

export default ViewBlogs;
