import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
  const [userCount, setUserCount] = useState(0);

  useEffect(() => {
    const fetchUserCount = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/count-users");
        const data = await res.json();
        setUserCount(data.count);
        localStorage.setItem("userCount", data.count);
      } catch (err) {
        console.error("Error fetching user count", err);
      }
    };

    fetchUserCount();
  }, []);

  return (
    <div className="container">
      <h2>Admin Dashboard</h2>
      <div className="row mt-4">
        <div className="col-md-4 mb-3">
          <div className="p-4 rounded bg-info text-white text-center shadow">
            <h4>Total Users</h4>
            <p className="fs-3 fw-bold">{userCount}</p>
          </div>
        </div>
        <div className="col-md-4 mb-3 d-flex align-items-center justify-content-center">
          <Link to="/admin/viewusers" className="btn btn-primary">Go</Link>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
