import React, { useState } from 'react'

const LoginForm = () => {
    const[email,setEmail]=useState("");
    const [password,setPassword]=useState("");

    const Submit=async(e)=>{
      e.preventDefault();
      const obj=JSON.stringify({
        Email:email,
        Password:password,
      })
      console.log(obj);

      try{
        const response=await fetch("http://localhost/api/user",{
          method:"POST",
          headers:{
            "Content-type":"application/json"
          },
          body:obj,
        });
        const data=await response.json();
        if(response.ok){
          alert("Login Successful");
          setEmail("");
          setPassword("");
        }
        else{
          alert("Login error");
        }
      }
      catch(err){
        console.log(err);
      }


    }

  return (
    <div className="row mt-5">
    <div className="col-lg-12 mt-5 d-flex flex-column align-items-center justify-content-center">
        <h3 className="m-3">
            <u>Login Form</u>
        </h3>
        <form onSubmit={Submit}>
            <div className="mb-3">
                <label htmlFor="exampleInputEmail1" className="form-label fs-5">
                    Email address
                </label>
                <input
                    type="email"
                    className="form-control"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    id="exampleInputEmail1"
                    aria-describedby="emailHelp"
                />
                <div id="emailHelp" className="form-text">
                    We'll never share your email with anyone else.
                </div>
            </div>
            <div className="mb-3">
                <label htmlFor="exampleInputPassword1" className="form-label fs-5">
                    Password
                </label>
                <input
                    type="password"
                    className="form-control"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    id="exampleInputPassword1"
                />
            </div>
            <button type="submit" className="btn btn-primary">
                Submit
            </button>
        </form>
    </div>
</div>
  )
}

export default LoginForm
