import { useState ,useEffect} from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ViewSmallsubCategoryinFront = () => {
  const { categoryname, subcategoryname } = useParams();  //route se le rha a
  console.log(categoryname);
  const [smallsubcategories,setSmallSubcategories]=useState([])

  useEffect(() => {
    axios.get(`http://localhost:5000/api/getsubsmallcategory/${categoryname}/${subcategoryname}`)
      .then(response => {
        setSmallSubcategories(response.data);
      })
      .catch(error => {
        console.error("Error fetching small subcategories:", error);
      });
  }, [categoryname, subcategoryname]);
  const addtocart=async(e)=>{
    
        e.preventDefault();
        
           alert("Add to Cart..."); // Show alert before submission
          try {
           
                const response = await fetch("http://localhost:5000/api/addtocart", {
                  method: "POST",
                  headers: { "Content-Type": "application/json" },
                  body: JSON.stringify({ subcategoryname,small }),
                });
        
            if (response.status === 201) {
             
              alert("Blog posted successfully"); // Success message
              setTitle("");
              setPhoto("");
              setContent("");
            }
          } catch (error) {
            console.error("Error submitting blog:", error);
            alert("Failed to submit blog");
          }
        

  }

  return (
    // <div className="container mt-4">
    //   <h2 className="text-center mb-4">Small Subcategories of {subcategoryname}</h2>
    //     <table className="table table-striped">
    //       <thead className="table-dark">
    //         <tr>
              
    //           <th>Small Subcategory Name</th>
    //           <th>Image</th>
    //         </tr>
    //       </thead>
    //       <tbody>
    //         {smallsubcategories.map((item) => (
    //           <tr key={item._id}>

    //             <td>{item.smallsubcategoryname}</td>
    //             <td>
    //               <img src={item.smallsubcategoryimage} alt={item.smallsubcategoryname} width="50" height="50" />
    //             </td>
    //           </tr>
    //         ))}
    //       </tbody>
    //     </table>
      
    // </div>
    <div className="container mt-4">
      <h2 className="text-center mb-4">Small Subcategories of {subcategoryname}</h2>
      <div className="row">
        {smallsubcategories.map((item) => (
          <div key={item._id} className="col-md-4 mb-3">
            <div className="card shadow-sm">
            <img
                src={item.smallsubcategoryimage}
                className="card-img-top"
                alt={item.smallsubcategoryname}
                style={{ height: "180px", objectFit: "cover", borderRadius: "10px", padding: "10px" }}
              />
              <div className="card-body text-center">
                <h5 className="card-title">{item.smallsubcategoryname}</h5>
              </div>
              <button className="btn btn-primary mt-2" onClick={()=>addtocart()}>Add to Cart</button>

            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
export default ViewSmallsubCategoryinFront;
