  //   fetchUserCount();
  // }, []);

  useEffect(() => {
  const fetchUserCount = async () => {
    const res = await fetch("http://localhost:5000/api/count-users");
    const data = await res.json();
    console.log("User Count:", data.count);
    // You can set it in state or localStorage
    localStorage.setItem("userCount", data.count);
  };

  fetchUserCount();
}, []);

  return (
    <div className="col py-3">
      <h3>Admin Dashboard</h3>
      <div className="card p-4 bg-light shadow-sm rounded">
        <h5>Total Users</h5>
        <h2>{userCount}</h2>
      </div>
    </div>
  );
};

export default AdminDashboard;
