<div className="d-flex">
      {/* Sidebar */}
      <nav
        className="sidebar bg-dark text-white min-vh-100 p-3"
        style={{
          width: "260px",
          position: "fixed",
          height: "100vh",
          boxShadow: "3px 0 10px rgba(0, 0, 0, 0.2)",
        }}
      >
        <h4 className="text-center py-3">Dashboard</h4>
        <ul className="nav flex-column">
          <li className="nav-item">
            <Link to="viewdashbord" className="nav-link text-white">
              <i className="bi bi-house-door me-2"></i> Home
            </Link>
          </li>
          <li className="nav-item">
            <a href="#submenu1" data-bs-toggle="collapse" className="nav-link text-white">
              <i className="fa-solid fa-table-columns me-2"></i> Dashboard
            </a>
            <ul className="collapse show nav flex-column ms-3" id="submenu1">
              <li><a href="#" className="nav-link text-white">Item 1</a></li>
              <li><a href="#" className="nav-link text-white">Item 2</a></li>
            </ul>
          </li>
          <li>
            <Link to="/orders" className="nav-link text-white">
              <i className="bi bi-table me-2"></i> Orders
            </Link>
          </li>
          <li>
            <a href="#submenu3" data-bs-toggle="collapse" className="nav-link text-white">
              <i className="bi bi-grid me-2"></i> Products
            </a>
            <ul className="collapse nav flex-column ms-3" id="submenu3">
              <li><a href="#" className="nav-link text-white">Product 1</a></li>
              <li><a href="#" className="nav-link text-white">Product 2</a></li>
              <li><a href="#" className="nav-link text-white">Product 3</a></li>
            </ul>
          </li>
          <li>
            <div className="dropdown">
              <a className="nav-link dropdown-toggle text-white" data-bs-toggle="dropdown">
                <i className="bi bi-book me-2"></i> Blogs
              </a>
              <ul className="dropdown-menu dropdown-menu-dark">
                <li><Link className="dropdown-item" to="viewBlogs">View Blogs</Link></li>
                <li><Link className="dropdown-item" to="addBlogs">Add Blogs</Link></li>
              </ul>
            </div>
          </li>
        </ul>

        {/* User Profile */}
        <div className="mt-auto p-3">
          <div className="dropdown">
            <a
              href="#"
              className="d-flex align-items-center text-white text-decoration-none dropdown-toggle"
              data-bs-toggle="dropdown"
            >
              <img
                src={photo || defaultPhoto}
                alt="User"
                width="30"
                height="30"
                className="rounded-circle"
              />
              <span className="ms-2">{item?.Email || "User"}</span>
            </a>
            <ul className="dropdown-menu dropdown-menu-dark">
              <li><Link className="dropdown-item" to="/profile">Profile</Link></li>
              <li><hr className="dropdown-divider" /></li>
              <li><a className="dropdown-item" href="#" onClick={handleSignOut}>Sign out</a></li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="content p-4" style={{ marginLeft: "260px", width: "100%" }}>
        <Outlet />
      </div>
    </div>
