import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [userCount, setUserCount] = useState(0);

  useEffect(() => {
    axios.get("/api/users/viewdashbord")
  .then(res => {
    const allUsers = res.data;
    const userCount = allUsers.filter(user => user.role === "user").length;

    // 2. LocalStorage me store bhi kar sakte ho if needed
    localStorage.setItem("allUsers", JSON.stringify(allUsers));

    // 3. Count use karo UI ke liye
    setUserCount(userCount);
  });
  }, []);

  return (
    <div className="col py-3">
      <h2>Admin Dashboard</h2>
      
      <div style={{
        display: 'flex',
        gap: '20px',
        marginTop: '30px'
      }}>
        {/* User Count Card */}
        <div style={{
          padding: '20px',
          borderRadius: '10px',
          backgroundColor: '#e3f2fd',
          minWidth: '200px',
          textAlign: 'center',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
        }}>
          <h4>Total Users</h4>
          <p style={{ fontSize: '24px', fontWeight: 'bold' }}>{userCount}</p>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
