import { useState ,useEffect} from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ViewSmallsubCategoryinFront = () => {
  const { categoryname, subcategoryname } = useParams();  //route se le rha a
  console.log(categoryname);
  const [smallsubcategories,setSmallSubcategories]=useState([])
  const [cartCount, setCartCount] = useState(0); // ✅ Add cart count state


  useEffect(() => {
    axios.get(`http://localhost:5000/api/getsubsmallcategory/${categoryname}/${subcategoryname}`)
      .then(response => {
        setSmallSubcategories(response.data);
      })
      .catch(error => {
        console.error("Error fetching small subcategories:", error);
      });
      // ✅ Fetch initial cart count from localStorage
    // const storedCartCount = localStorage.getItem("cartCount") || 0;
    // setCartCount(parseInt(storedCartCount));
  }, [categoryname, subcategoryname]);
  useEffect(() => {
    const savedCartCount = localStorage.getItem("cartCount");
    if (savedCartCount) {
      setCartCount(parseInt(savedCartCount, 10));  // Convert to number
    }
  }, []);
  

  


  const addtocart=async(smallsubcategoryname,smallsubcategoryimage,smallsubcategorycontent)=>{
    // e.preventDefault();
        
           alert("Add to Cart..."); // Show alert before submission
          try {
           
                const response = await fetch("http://localhost:5000/api/addtocart", {
                  method: "POST",
                  headers: { "Content-Type": "application/json" },
                  body: JSON.stringify({ subcategoryname,smallsubcategoryname:smallsubcategoryname, smallsubcategoryimage:smallsubcategoryimage,Price:smallsubcategorycontent }),
                });
        
            if (response.status === 201) {
             
              alert("add to cart successfully"); // Success message
              // ✅ Increase cart count
        // const newCartCount = cartCount + 1;
        // setCartCount(newCartCount);
        // localStorage.setItem("cartCount", newCartCount);
        setCartCount((prevCount) => {
          const newCount = prevCount + 1;
          localStorage.setItem("cartCount", newCount);
          return newCount;
        });
            }
          } catch (error) {
            console.error("Error adding to cart:", error);
            alert("Failed to submit blog");
          }
        

  }

  return (
    
    <div className="container mt-4">
      <h2 className="text-center mb-4">Small Subcategories of {subcategoryname}</h2>
      <div className="row">
        {smallsubcategories.map((item) => (
          <div key={item._id} className="col-md-4 mb-3">
            <div className="card shadow-sm">
            <img
                src={item.smallsubcategoryimage}
                className="card-img-top"
                alt={item.smallsubcategoryname}
                style={{ height: "180px", objectFit: "cover", borderRadius: "10px", padding: "10px" }}
              />
              <div className="card-body text-center">
                <h5 className="card-title">{item.smallsubcategoryname}</h5>
              </div>
              <div className="card-body text-center">
                <h5 className="card-title">{item.smallsubcategorycontent}</h5>
              </div>
              <button className="btn btn-primary mt-2" onClick={()=>addtocart(item.smallsubcategoryname,item.smallsubcategoryimage,item.smallsubcategorycontent)}>Add to Cart</button>

            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
export default ViewSmallsubCategoryinFront;
