import { useEffect, useState } from "react";

const ViewAddtocart = () => {
  const [cart, setCart] = useState([]);
  const [cartcount, setcartcount] = useState(0); // Initialize cartcount state


  // useEffect(() => {
  //   const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
